import React, { useState, useEffect } from 'react';
import './styles.scss';
import mountain1 from '../../assets/mountain1.svg';
import mountain2 from '../../assets/mountain2.svg';

function Weinfest() {

    const storyPoints = [
        // Introduction/ Hook
        {
            text: "Welcome stranger!",
            position: 0, // Start
        },
        {
            text: "I’m Robin, a visitor from a magical place in the south of Germany.",
            position: 0,
            mountainTop: 40,
            mountainBottom: 60,
        },
        {
            text: "What might I call you?",
            position: 0,
            action: "text",
            mountainTop: 20,
            mountainBottom: 10,
        },
        {
            text: "Hello [Name], nice to meet you.",
            position: 0,
        },
        {
            text: "I’m a bit of a storyteller. Have you ever heard the tale of the three wine brothers?",
            position: 0,
            action: "decision",
        },
        {
            text: "Well, a long time ago, there were three brothers.",
            position: 20,
        },
        {
            text: "Their parents were makers of fine wines.",
            position: 320,
        },
        {
            text: "The boys often played in the mystical vineyards.",
            position: 820,
        },
        {
            text: "One day, they found a golden grape.",
            position: 2020, // Gold = 2020
        },
        {
            text: "Curious as they were, they picked it.",
            position: 2320,
        },
        {
            text: "Suddenly, a glowing door opened in midair and they entered a magical world.",
            position: 2600,
        },
        {
            text: "Green hills covered in golden sunlight rose before them.",
            position: 2920, // Mountain appears
            mountain: true,
        },
        {
            text: "The air was full of laughter, singing and loud voices.",
            position: 3720,
        },
        {
            text: "Following the sounds they found themselves on a busy marketplace.",
            position: 4720,
        },
        {
            text: "Everyone was drinking from big glasses of wine. They couldn’t believe how cheap it was.",
            position: 6200, // Midday
        },
        {
            text: "The three brothers loved it here and decided to stay.",
            position: 7400,
        },
        {
            text: "That’s how they founded their own wine village called Mußbach.",
            position: 7900,
        },
        {
            text: "A village where time slows down and strangers become friends.",
            position: 8900,
        },
        {
            text: "This year, you are given the golden grape to enter that magical place.",
            position: 10000,
        },
        {
            text: "On Friday take your time to arrive and settle.",
            position: 11500,
        },
        {
            text: "Have a good night sleep from a work week and train ride.",
            position: 13100,
        },
        {
            text: "After a slow Saturday morning we embark on our first adventure. Green hills are calling for a wine expedition.",
            position: 15000,
        },
        {
            text: "Regional wine and delicious regional food awaits us at the hill top.",
            position: 16000,
        },
        {
            text: "That’s where we hang out until we are reenergized. ",
            position: 17000,
        },
        {
            text: "The way down everything seems a little bit easier.",
            position: 18000,
        },
        {
            text: "Before you notice we’re back home to enjoy a nap in the hammock.",
            position: 20000,
        },
        {
            text: "When the sun sets, the vibrating wine festival begins.",
            position: 22000,
        },
        {
            text: "Maybe you can already hear the laughing and signing which once attracted the three young brothers?",
            position: 22700,
        },
        {
            text: "Together we walk to the town centre of Mußbach. That’s where our next adventure awaits.",
            position: 23000,
        },
        {
            text: "Drinking and laughing together.",
            position: 24000,
        },
        {
            text: "By the way, what type of wine do you prefer?",
            position: 24500,
            type: "decision",
        },
        {
            text: "Good choice [Name]! Let’s hope there is enough for everyone…",
            position: 25500,
        },
        {
            text: "But who knows where the night will end?",
            position: 26500, // Night
        },
        {
            text: "Fast forward. The next morning might be a bit hard. But believe me, it was worth it.",
            position: 29000, // Morning
        },
        {
            text: "Finally, even in the tiny village of Mußbach time is passing, however slowly.",
            position: 29500, // Midday
        },
        {
            text: "It’s already time to leave. Back to another reality.",
            position: 30000,
        },
        {
            text: "It’s still a couple of months to the 03 July 2020.",
            position: 31000,
        },
        {
            text: "Are you curious to be part of this event?",
            position: 32000,
        },
        {
            text: "Sign up <a href=''>here</a> and I’ll let you know once things get more serious. ",
            position: 32500,
        },
        {
            text: "Just be sure to block the 03 to 05 July in your calendar.",
            position: 33000,
        },
        {
            text: "You know - just in case.",
            position: 33000,
        },
        {
            text: "Sincerely yours, <br><span style='font-familiy: Comic-Sans'>Sascha</span>",
            position: 33000,
        },
        {
            text: "THE END",
            position: 33000,
        },

        // THE END SLIDE
    ]  

    const [storyPoint, setStoryPoint] = useState(storyPoints[0]);
    const [storyIndex, setStoryIndex] = useState(1);
    const [isHidden, setIsHidden] = useState(false);

    useEffect(() => {

        document.onkeydown = (event) => {

            if (event.keyCode === 39)
                changeScene('forward');
            else if (event.keyCode === 37)
                changeScene('backward')
        };
    });

    const changeScene = (direction = 'forward') => {

        // Hide text and change it
        if (storyIndex < storyPoints.length - 1 && storyIndex > 0)
            setIsHidden(true);

        let contentElement = document.getElementById('weinfest-content');
        contentElement.style.marginLeft = '-' + storyPoints[storyIndex].position + 'px';

        setTimeout(() => {
            // Change story point
            if (direction === 'forward' && storyIndex < storyPoints.length - 1)
                setStoryIndex(storyIndex + 1);
            else if (direction === 'backward' && storyIndex > 0)
                setStoryIndex(storyIndex - 1);

            setStoryPoint(storyPoints[storyIndex]);
            setIsHidden(false)
        }, 700);
    }

    return (
        <>         
            <div className='weinfest-page evening' onClick={() => changeScene('forward')}>
                <span className={'story ' + (isHidden ? ' hide-story' : ' show-story')}>
                    {storyPoint.text}
                </span>
                <img className={'mountain mountain-bottom'} alt='mountain-bottom' style={{bottom: storyPoint.mountainBottom ? -storyPoint.mountainBottom : -300}} src={mountain1}/>
                <img className={'mountain mountain-top'} alt='mountain-top' style={{bottom: storyPoint.mountainBottom ? -storyPoint.mountainBottom : -300}} src={mountain2}/>
                <div id='weinfest-content'>

                </div>
            </div>
        </>
    );
};

export default Weinfest;
