import React from 'react';
import './styles.scss';

import noGif from '../../assets/no.gif';
import goGif from '../../assets/go.gif';

function RSVP() {

  const sendNo = () => {

  }

  const sendGo =() => {

      var link = "mailto:sascha.bardua@hotmail.de?"
               + "&subject=I'M COMING!!!"
               + "&body=Dear Sascha, %0D%0A I am super excited to confirm my participation to this incredible event. %0D%0A %0D%0A I hereby agree to the potential risks of a good party and am willing to accept these."
      ;

      window.location.href = link;
  }

  return (
    <div className="rsvp-wrapper">
      <div className="no-wrapper half" onClick={sendNo}>
        <span className="no">
          <img src={noGif} />
          <span className="rsvp-text">NO :/</span>
        </span>
      </div>

      <div className="go-wrapper half" onClick={sendGo}>
        <span className="go">
          <img src={goGif} />
          <span className="rsvp-text">GO :}</span>
        </span>
      </div>
    </div>
  );
}

export default RSVP;
