import React, { useState, useEffect } from 'react';
import Header from './header';
import Contact from './contact';
import Agenda from './agenda';
import './styles.scss';

function Weinfest() {

    const [isInitial, setIsInitial] = useState(true);
    const [hasAccess, setHasAccess] = useState(true);   // TODO change
    const [isHidden, setIsHidden] = useState(false);

    useEffect(() => {

        //window.scrollTo(0,0);

        /*
        if (isInitial) {
            let code = prompt("What is your access code for the wine festival?");

            if (code === "schorle") {
                setIsInitial(false);
                setHasAccess(true);
            }
        }*/
    });

    const scrollDelay = (delay) => {
        return new Promise(res => setTimeout(res, delay));
    }

    const changeScene = async() => {

        // Hide text and change it
        setIsHidden(true);

        let totalDuration = 1000;
        let pixelsToScroll = 2000;
        let delay = 100;
        let steps = pixelsToScroll / (totalDuration / delay);
        let movement = pixelsToScroll / steps;

        // Scroll effect
        for (let x = 0; x <= steps; x++) {
            window.scrollBy({ top: 0, left: movement, behavior: 'smooth' });
            await scrollDelay(delay);
        }

        setTimeout(() => {
            setIsHidden(false)
        }, 2000)
    }

    return (
        <>         
            <div className="weinfest-page evening">
                <div className="weinfest-content">
                    <span className={"story " + (isHidden ? " hide-story" : " show-story")} onClick={changeScene}>Guten Morgen!</span>
                </div>
            </div>
        </>
    );
}

export default Weinfest;
