import React, { useState } from 'react';
import './styles.scss';

function Header() {

    const [name, setName] = useState("Mr. Or Mrs. Random");

    const getInviteeName = () => {

        const searchParams = new URLSearchParams(window.location.search);

        return searchParams.get('name') || name;
    }

    return (
        <div className="header-wrapper">
            <h1>Dear {getInviteeName()},</h1>
            <h2>every year we celebrate a wine festival in my hometown. </h2>
            <h2>I would love to invite you over for a weekend at my parents place and experiencing a truely cultural event.</h2>
            <h2>The weekend in question is <span className="highlight-text">03. - 05. July.***</span></h2>
            <h3>* You are great and beautiful.</h3>
            <h3>** Well not really, I have four great roomies.</h3>
            <h3>*** It's a multi-day event. Guests are welcome to arrive on friday. Sleeping areas will be provided according to demand.</h3>
        </div>
    );
}

export default Header;
