import React, { useState, useEffect } from 'react';
import Header from './header';
import Contact from './contact';
import Agenda from './agenda';
import './styles.scss';

function Weinfest() {

    const storyPoints = [
        // Introduction/ Hook
        {
            text: "Welcome stranger!",
            position: 0,
        },
        {
            text: "I’m Robin, a visitor from a magical place in the south of Germany.",
            position: 0,
        },
        {
            text: "What might I call you?",
            position: 0,
            action: "text"
        },
        {
            text: "Hello [Name], nice to meet you.",
            position: 0,
        },
        {
            text: "I’m a bit of a storyteller. Have you ever heard the tale of the three wine brothers?",
            position: 0,
            action: "decision",
        },
        {
            text: "Well, a long time ago, there were three brothers.",
            position: 0,
        },
        {
            text: "Their parents were makers of fine wines.",
            position: 0,
        },
        {
            text: "The boys often played in the mystical vineyards.",
            position: 0,
        },
        {
            text: "One day, they found a golden grape.",
            position: 0,
        },
        {
            text: "Curious as they were, they picked it.",
            position: 0,
        },
        {
            text: "Suddenly, a glowing door opened in midair and they entered a magical world.",
            position: 0,
        },
        {
            text: "Green hills covered in golden sunlight rose before them.",
            position: 0,
        },
        {
            text: "The air was full of laughter, singing and loud voices.",
            position: 0,
        },
        {
            text: "Following the sounds they found themselves on a busy marketplace.",
            position: 0,
        },
        {
            text: "Everyone was drinking from big glasses of wine. They couldn’t believe how cheap it was.",
            position: 2000, // Midday
        },
        {
            text: "The three brothers loved it here and decided to stay. ",
            position: 2200,
        },
        {
            text: "That’s how they founded their own wine village called Mußbach.",
            position: 2300,
        },
        {
            text: "A village where time slows down and strangers become friends.",
            position: 2600,
        },
        {
            text: "This year, you are given the golden grape to enter that magical place to experience a memorable weekend.",
            position: 4000, // Evening
        },
        {
            text: "On the first day take your time to arrive and settle.",
            position: 4200,
        },
        {
            text: "Have a good night sleep from a work week and train ride.",
            position: 0, // Night
        },
        {
            text: "After a slow morning we embark on our first adventure. Green hills are calling for a wine expedition.",
            position: 5000, // Morning
        },
        {
            text: "Regional wine and delicious regional food awaits us at the hill top.",
            position: 5000, // Midday
        },
        {
            text: "That’s where we hang out until we are re-energized. ",
            position: 5000,
        },
        {
            text: "The way down everything seems a little easier.",
            position: 5500,
        },
        {
            text: "Before you notice we’re back home to enjoy a nap in the hammock.",
            position: 5800,
        },
        {
            text: "When the sun sets, the vibrating wine festival begins.",
            position: 7000, // Evening
        },
        {
            text: "Maybe you can already hear the laughing and signing which once attracted the three young brothers?",
            position: 7000,
        },
        {
            text: "Together we walk to the town centre of Mußbach. That’s where our next adventure awaits.",
            position: 0,
        },
        {
            text: "Drinking and laughing together.",
            position: 0,
        },
        {
            text: "But wait,… </br>What type of wine do you prefer?",
            position: 0,
            type: "decision",
        },
        {
            text: "Good choice [Name]! Let’s hope there is enough for everyone…",
            position: 0,
        },
        {
            text: "But who knows where the night will end?",
            position: 0, // Night
        },
        {
            text: "Fast forward. The next morning might be a bit hard. But believe me, it was worth it.",
            position: 0, // Morning
        },
        {
            text: "Finally, even in the tiny village of Mußbach time is passing, however slowly.",
            position: 0, // Midday
        },
        {
            text: "It’s already time to leave. Back to another reality.",
            position: 0,
        },
        {
            text: "It’s still a couple of months to the 03 July 2020.",
            position: 0,
        },
        {
            text: "But are you curious to be part of this event?",
            position: 0,
        },
        {
            text: "Sign up <a href=''>here</a> and I’ll let you know once things get more serious. ",
            position: 0,
        },
        {
            text: "Just be sure to block the 03 to 05 July in you calendar.",
            position: 0,
        },
        {
            text: "You know - just in case.",
            position: 0,
        },
        {
            text: "Sincerely yours, <br><span style='font-familiy: Comic-Sans'>Sascha</span>",
            position: 0,
        },
    ]  

    const [storyPoint, setStoryPoint] = useState(storyPoints[0]);
    const [storyIndex, setStoryIndex] = useState(0);
    const [isHidden, setIsHidden] = useState(false);

    useEffect(() => {

        //window.scrollTo(0,0);

        document.onkeydown = (event) => {
            console.log(event.keyCode);
            nextScene();
        };

        /*
        if (isInitial) {
            let code = prompt("What is your access code for the wine festival?");

            if (code === "schorle") {
                setIsInitial(false);
                setHasAccess(true);
            }
        }*/
    });



    const scrollDelay = (delay) => {
        return new Promise(res => setTimeout(res, delay));
    };

    const nextScene = async() => {

        // Hide text and change it
        setIsHidden(true);

        let totalDuration = 1800;
        let pixelsToScroll = storyPoints[storyIndex].position;
        let delay = 100;
        let steps = totalDuration / delay;
        let movement = pixelsToScroll / steps;

        // Scroll effect
        for (let x = 0; x <= steps; x++) {
            window.scrollBy({ top: 0, left: movement, behavior: 'smooth' });
            await scrollDelay(delay);

            // Show after half scroll
            if (x === steps/2) {
                // Next story point
                setStoryIndex(storyIndex + 1);
                setStoryPoint(storyPoints[storyIndex]);
                setIsHidden(false)
            }
        }
    };

    return (
        <>         
            <div className="weinfest-page evening">
                <span className={"story " + (isHidden ? " hide-story" : " show-story")} onClick={nextScene}>
                    {storyPoint.text}
                </span>
                <div className="weinfest-content">

                </div>
            </div>
        </>
    );
};

export default Weinfest;
